insert into user_order (id, user_id, activity_id, 
  minu_price, help_id, create_time, 
  update_time, state, qr_code
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{activityId,jdbcType=INTEGER}, 
  #{minuPrice,jdbcType=DECIMAL}, #{helpId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=INTEGER}, #{qrCode,jdbcType=VARCHAR}
  )

mapper.select:
select uo.id,uo.user_id as userId,uo.minu_price as minuPrice,a.title,a.picture,u.nickname as nickName
from user_order uo left join activity a on uo.activity_id=a.id left join user u on uo.user_id = u.id
where 1=1
and uo.state =1
and a.enterprise_id=#{id}

  limit #{page.start},#{page.pageSize}

